// export default App;
import React, { createContext, useContext, useEffect, useState } from 'react';
import { createBrowserRouter, RouterProvider, Route, Link, useParams, useNavigate, useLocation } from 'react-router-dom';
import logo from './logo.svg';
import './App.css';

// Create a context for navigation
const NavigationContext = createContext();

// NavigationProvider component to track previous location
export function NavigationProvider({ children }) {
  const location = useLocation();
  const [previousLocation, setPreviousLocation] = useState(null);

  useEffect(() => {
    setPreviousLocation(location.pathname);
  }, [location]);

  return (
    <NavigationContext.Provider value={previousLocation}>
      {children}
    </NavigationContext.Provider>
  );
}

// Custom hook to use the previous location
export function usePreviousLocation() {
  return useContext(NavigationContext);
}

const fruitList = [
  { title: "Potato", isFruit: false, id: 1 },
  { title: "Garlic", isFruit: false, id: 2 },
  { title: "Apple", isFruit: true, id: 3 },
];

// FruitList component
function FruitList() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h1>Fruit List</h1>
        <ul>
          {fruitList.map(fruit => (
            <li key={fruit.id}>
              {fruit.title + "\t"}
              <Link
                to={`/fruit/${fruit.id}`}
                style={{ color: 'yellowgreen', fontSize: '12px' }}
                state={{ from: '/' }} 
              >
                Details
              </Link>
            </li>
          ))}
        </ul>
      </header>
    </div>
  );
}

// FruitDetail component
function FruitDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const fruit = fruitList.find(p => p.id === parseInt(id));
  const previousLocation = usePreviousLocation();

  if (!fruit) {
    return <div>Fruit not found</div>;
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h1>Fruit Details</h1>
        <p>Id: {id}</p>
        <p>Title: {fruit.title}</p>
        <p>Is Fruit: {fruit.isFruit ? 'Yes' : 'No'}</p>
        <Link 
          to={`/`} 
          style={{ color: 'yellowgreen', fontSize: '12px' }}
          onClick={() => navigate(previousLocation || '/')}
        >
          Back To Home
        </Link>
        <p>Previous location: {previousLocation || 'No previous location'}</p>
      </header>
    </div>
  );
}

// Set up the router
const router = createBrowserRouter([
  {
    path: '/',
    element: <FruitList />,
  },
  {
    path: 'fruit/:id',
    element: <FruitDetail />,
  },
]);

// Main App component
function App() {
  return (
    <NavigationProvider>
      <RouterProvider router={router} />
    </NavigationProvider>
  );
}

export default App;
