import logo from './logo.svg';
import './App.css';
import React from 'react';
import { createBrowserRouter, RouterProvider, Route, Link, useParams } from 'react-router-dom';


const router = createBrowserRouter([
  {
    path: '/',
    element: <fruitListList />,
  },
  {
    path: 'fruitList/:id',
    element: <fruitListDetail />,
  },
]);
const fruitList = [
  { title: "Potato", isFruit: false, id: 1 },
  { title: "Garlic", isFruit: false, id: 2 },
  { title: "Apple", isFruit: true, id: 3 },
];
function fruitListList(){
  return (
    <div>
      <h1>fruitList List</h1>
      <ul>
        {fruitList.map(fruitList => (
          <li key={fruitList.id}>
            {fruitList.title}
            <Link to={`/fruitList/${fruitList.id}`}>
              <button>View Details</button>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

function fruitListDetail(){
  const { id } = useParams();
  const fruitList = fruitList.find(p => p.id === parseInt(id));

  if (!fruitList) {
    return <div>fruitList not found</div>;
  }

  return (
    <div>
      <h1>fruitList Details</h1>
      <p>Title: {fruitList.title}</p>
      <p>Is Fruit: {fruitList.isFruit ? 'Yes' : 'No'}</p>
      <p>Id: {id}</p>
    </div>
  );
};





function App(){
  return (
    <RouterProvider router={router} />
  );
};


export default App;





























