import logo from './logo.svg';
import './App.css';
import React from 'react';
import { createBrowserRouter, RouterProvider, Route, Link, useParams } from 'react-router-dom';


const router = createBrowserRouter([
  {
    path: '/',
    element: <FruitList />,
  },
  {
    path: 'product/:id',
    element: <FruitDetail />,
  },
]);
const fruitList = [
  { title: "Potato", isFruit: false, id: 1 },
  { title: "Garlic", isFruit: false, id: 2 },
  { title: "Apple", isFruit: true, id: 3 },
];
function FruitList(){
  return (
    <div>
      <h1>Fruit List</h1>
      <ul>
        {fruitList.map(product => (
          <li key={product.id}>
            {product.title}
            <Link to={`/product/${product.id}`}>
              <button>View Details</button>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

function FruitDetail(){
  const { id } = useParams();
  const product = fruitList.find(p => p.id === parseInt(id));

  if (!product) {
    return <div>Product not found</div>;
  }

  return (
    <div>
      <h1>Fruit Details</h1>
      <p>Title: {product.title}</p>
      <p>Is Fruit: {product.isFruit ? 'Yes' : 'No'}</p>
      <p>Id: {id}</p>
    </div>
  );
};





function App(){
  return (
    <RouterProvider router={router} />
  );
};


export default App;





























